mapper.update:
UPDATE blog.weibo
SET content = #{content},
    created = #{created}
WHERE id = #{id}

mapper.select:
SELECT id, content, created, user_id userId
FROM blog.weibo
WHERE id = #{id}

SELECT content, created
FROM blog.weibo
WHERE id = #{id}

SELECT id, content, created
FROM blog.weibo
WHERE user_id = #{userId}

SELECT id, content, user_id
FROM blog.comment
WHERE weibo_id = #{id}
